const assert = require('assert');
const sinon = require('sinon');
const Converter = require('../src/api/converter');

describe('Unit - Converter', () => {
  let sharp;
  let s3;
  let environment;

  afterEach(() => {
    sinon.resetHistory();
  });

  it('Should convert base64 to an S3 entry', async () => {
    sharp = sinon.stub().returns({ jpeg: sinon.stub(), toBuffer: sinon.stub().returns(Buffer.from('string')) });
    s3 = {
      putObject: sinon.stub().returns({ promise: sinon.stub().resolves() })
    };
    environment = {
      getFileBucket: sinon.stub().returns('bucket')
    };

    const converter = new Converter({ sharp, s3, environment });
    // eslint-disable-next-line max-len
    const base64 = '';
    const result = await converter.convertAndStore(base64, 'application/pdf');
    assert.ok(result.includes('.pdf'));
    assert.deepStrictEqual(sharp.callCount, 0);
    assert.deepStrictEqual(s3.putObject.callCount, 1);
  });

  it('Should convert base64 to an S3 entry', async () => {
    sharp = sinon.stub().returns({ jpeg: sinon.stub().returns({ toBuffer: sinon.stub().returns(Buffer.from('string')) }) });
    s3 = {
      putObject: sinon.stub().returns({ promise: sinon.stub().resolves() })
    };
    environment = {
      getFileBucket: sinon.stub().returns('bucket')
    };

    const converter = new Converter({ sharp, s3, environment });
    // eslint-disable-next-line max-len
    const base64 = '';
    const result = await converter.convertAndStore(base64, 'image/tiff');
    assert.ok(result.includes('.jpg'));
    assert.deepStrictEqual(sharp.callCount, 1);
    assert.deepStrictEqual(s3.putObject.callCount, 1);
  });
});
